import React from 'react';
import { useHistory } from 'react-router-dom'; // If using React Router
import Cookies from 'js-cookie';
import './css/styles.css';

function HomePage() {
  const history = useHistory(); // If using React Router

  const handleLogout = (e) => {
    e.preventDefault();

    // Clear cookies
    Cookies.remove('demo-cookie'); // Replace with your actual cookie name

    // Clear session storage or local storage if used
    sessionStorage.clear();
    localStorage.clear();

    // Redirect to the index page
    history.push('/'); // If using React Router
    // If not using React Router, you can use window.location.href = '/';
  };

  return (
    <div className="centered">
      <a className="logo" href="/">
        <img src="/images/wso2-logo.webp" alt="WSO2 Logo" width="100" />
      </a>
      <h2>You Have Logged In!</h2>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default HomePage;
